import React, { useState } from 'react'
import { Link, navigate } from '@reach/router'
import {
    Paper,
    FormControl,
    InputLabel,
    OutlinedInput,
    Button,
    makeStyles
} from '@material-ui/core';
import axios from 'axios';

import MenuItem from '@material-ui/core/MenuItem';

import Select from '@material-ui/core/Select';

const useStyles = makeStyles({
    paper: {
        width: "60rem", padding: "1rem",
        margin: "auto"
    },
    input: {
        marginBottom: "1rem"
    },
    button: {
        width: "100%",
        display: 'block',
        marginTop: theme.spacing(2),
    },
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },
});

export default () => {
    const classes = useStyles();
    const [name, setName] = useState("");
    const [ position, setPosition ] = useState("")
    const [ chests, setChests ] = useState();
    const [ imageUrl, setImageUrl ] = useState("");
    const [ errors, setErrors ] = useState([]);
    const [ open, setOpen ] = useState(false);

    const handleChange = (e) => {
        setChests(e.target.value);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleOpen = () => {
        setOpen(true);
    };


    return (
        <Paper elevation={3} style={classes.paper}>

                <form onSubmit = { createPirate } >
                {
                    errors?
                        (errors.map((err, index) => <p key={index}>{err}</p>)):
                        ""
                }
                    <FormControl variant="outlined" style={classes.input}>
                        <InputLabel>Pirate Name</InputLabel>
                        <OutlinedInput type="text"
                                        name="name"
                                        value={name}
                                        onChange = { (e) => setName(e.target.value)}
                                        />
                    </FormControl>
                    <br />

                    <FormControl variant="outlined" style={styles.input}>
                        <InputLabel>Image URL</InputLabel>
                        <OutlinedInput type="text"
                                        name="imageUrl"
                                        value={imageUrl}
                                        onChange = {(e) => setImageUrl(e.target.value)}
                                        />
                    </FormControl>
                    <br />

                    <FormControl className={classes.formControl}>
                        <InputLabel id="demo-controlled-open-select-label">Age</InputLabel>
                        <Select
                            labelId="demo-controlled-open-select-label"
                            id="demo-controlled-open-select"
                            open={open}
                            onClose={handleClose}
                            onOpen={handleOpen}
                            value={chests}
                            onChange={handleChange}
                            >
                            <MenuItem value="">
                                <em>None</em>
                            </MenuItem>
                            <MenuItem value={0}>0</MenuItem>
                            <MenuItem value={1}>1</MenuItem>
                            <MenuItem value={2}>2</MenuItem>
                            <MenuItem value={3}>3</MenuItem>
                            <MenuItem value={4}>4</MenuItem>
                            <MenuItem value={5}>5</MenuItem>
                            <MenuItem value={6}>6</MenuItem>
                        </Select>
                    </FormControl>

                    <FormControl variant="outlined" style={styles.input}>
                        <InputLabel>Image URL</InputLabel>
                        <OutlinedInput type="text"
                                        name="imageUrl"
                                        value={imageUrl}
                                        onChange = {(e) => setImageUrl(e.target.value)}
                                        />
                    </FormControl>
                    <br />

                    <Button type="submit" variant="contained" color="primary" disabled={!(playerName.length >= 2)}>
                        ADD
                    </Button>

                </form>
            </Paper>

    )
}
