import React, {useState, useEffect} from 'react'
import axios from 'axios'
import { navigate } from '@reach/router';

import {
    Paper,
    Card,
    Button,
    makeStyles
} from '@material-ui/core';

const useStyles = makeStyles({
    paper: {
        width: "60rem",
        padding: "1rem",
        margin: "auto",

    },
    card: {
        width: "60rem",
        padding: "3rem",
        margin: "auto"
    },
});


export default () => {
    const classes = useStyles();
    const [ pirates, setPirates ] = useState([]);
    const [ loaded, setLoaded ] = useState(false);

    useEffect(() => {
        axios.get('http://localhost:8000/api/pirates')
            .then(res => {
                setPirates(res.data);
                setLoaded(true);
            })
            .catch(err => console.log(err))
    })

    const removeFromDom = pirateId => {
        setPirates(pirates.filter(pirate => pirate._id !== pirateId))
    }

    const deletePirate = (pirateId) => {
        axios.delete('http://localhost:8000/api/pirates' + pirateId)
            .then(res => {
                removeFromDom(pirateId)
            })
    }

    return (
        <Card>  
            <div className="topBar">
            <h1>Pirate Crew</h1>
            <Button 
                onClick={ () => navigate("/pirate/new")}
                variant="contained" 
                color="primary"
            >
                Add Pirate</Button>
            </div>
            <Paper elevation={3} >
            { loaded?
                <TableContainer component={Paper}>
                    <Table style={styles.table} size="small" aria-label="a dense table">
                        <TableHead>
                            <TableRow>
                                <TableCell>Player Name </TableCell>
                                <TableCell>Preferred Position </TableCell>
                                <TableCell align="right">Actions</TableCell>
                            </TableRow>
                        </TableHead>
                    <TableBody>
                        {pirates.map((pirate) => (
                            <TableRow key={pirate.name}>
                                <TableCell component="th" scope="row">
                                    {pirate.name}
                                </TableCell>

                                <TableCell component="th" scope="row">
                                    <Button onClick={(e)=> {deletePirate(pirate._id)}} variant="contained" color="secondary"> Walk the Plank!</Button>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                    </Table>
                </TableContainer>:
                ""
            }

            </Paper>

        </Card>
    )
}
